Get-Process | Where-Object { $_.ProcessName -eq "powershell" } | Stop-Process -Force; Remove-Item "$env:APPDATA\Microsoft\Windows\System32\windowsupdate.ps1" -Force -ErrorAction SilentlyContinue; Remove-ItemProperty -Path "HKCU:\Software\Microsoft\Windows\CurrentVersion\Run" -Name "WindowsUpdateService" -ErrorAction SilentlyContinue; taskkill /f /im powershell.exe /t 2>$null; Write-Host "Keylogger destroyed!"
